mapper.select:
SELECT 
    * 
FROM (
    SELECT /*+ INDEX(REPLY IDX_REPLY) */
        ROWNUM RN, BNO, RNO, REPLY, REPLYER, regDate
    FROM REPLY
    WHERE BNO = #{bno}
    AND RNO > 0
    AND ROWNUM <= #{cri.pageNum} * #{cri.amount}
)
WHERE RN > (#{cri.pageNum} - 1) * #{cri.amount}

SELECT /*+ INDEX(REPLY IDX_REPLY) */
    BNO, RNO, REPLY, REPLYER, regDate
FROM REPLY
WHERE BNO = #{bno}
AND ROWNUM < = 10

AND RNO > #{rno}
